mapper.select:
select nvl(COUNT(*), 0)
from guest
where blogNum=#{blogNum}

select nvl(COUNT(*), 0)
from guestreply
where guestNum=#{guestNum}

select userId, c.category, boardNum, subject, content, to_char(created, 'YYYY.MM.DD') created, b.enabled from board b join category c on c.categoryNum=b.categoryNum where userId=#{userId}
and c.category=#{category}
order by boardNum desc OFFSET #{offset} ROWS FETCH FIRST #{rows} ROWS ONLY

select t.tag, t.tagNum, b.boardNum 
from board b
left outer join (
    select boardNum, tagNum from board_tag
) tb on b.boardNum=tb.boardNum
left outer join (
    select tagNum, tag from tag
) t on t.tagNum=tb.tagnum
where b.boardNum=#{boardNum}
order by t.tagNum asc

SELECT NVL(COUNT(*), 0)
FROM board b
join category c on c.categoryNum=b.categoryNum
where userId=#{userId}

	and c.category=#{category}

select NVL(COUNT(*), 0)
from boardReply
where boardNum=#{boardNum}

select NVL(Max(userNick), 'X') replyUser from member1 m
join boardReply r on r.userId=m.userId
where replyNum=#{replyType}
